-- Pick between 2 sitting animations --
function UF_SittingAnims(idActor, idAnim01, idAnim02)

T_ANIMSET_OVERRIDES = {
[1] = idAnim01,
[2] = idAnim02}

S_SIT_ANIM = T_ANIMSET_OVERRIDES[Hydra.GetRandomInt(1,2)]
Hydra.QActorPower(idActor, S_SIT_ANIM)

end
