function Start(sEventName, idActor, nUserParam)
	Hydra.DisplayGameMessageForAllQuestParticipants("Messages:USZ_RoamingFlySpawnWarning");

	-- Send event to roaming boss so boss can register Helltide is active and periodically check if it active
	-- When no longer active it can let itself escape
	-- This is a workaround for issue with this Quest unable to send event Helltide is over in Shutdown or ending phase reliably
	Hydra.EventSend("Helltide_RoamingBoss_StartCheckQuestActive");
end

function Start(sEventName, idActor, nUserParam)
	idWorldBoss = idActor;

	if Hydra.ActorIsAlive(idWorldBoss) then
		-- Notify Power Erruption Owner(s) that World Boss is Active
		-- For ship 1-0-0, this will disable spawning monsters for Perf
		local tPowerErruptionTargets = Hydra.PlayerActors();
		if #tPowerErruptionTargets > 0 then
			for i, idPowerErruptionTarget in ipairs(tPowerErruptionTargets) do
				if Hydra.ActorIsValid(idPowerErruptionTarget) then
					Hydra.BuffApplyToTarget(POWER_ERUPTION, S_POWER_ERRUPTION_BUFF_WORLDBOSS_ACTIVE, idPowerErruptionTarget);
					
					-- Store World Boss ID to applied Buff for future use such as checking when World Boss is killed or escaped
					Hydra.BuffSetStoredData(idPowerErruptionTarget, POWER_ERUPTION, S_POWER_ERRUPTION_BUFF_WORLDBOSS_ACTIVE, idWorldBoss);
				end
			end
		end

		-- Despawn Monsters spawned by Power Erruption
		DespawnPowerEruptionMonstersFromWorldBoss();
	end
end

