USER_EVENT_USZ_END = "UBERSUBZONE_END"

-- World Boss
S_EVENT_WORLDBOSS_PING = "EVENT_WORLDBOSS_PING";
S_EVENT_WORLDBOSS_STARTED = "EVENT_WORLDBOSS_STARTED";
idWorldBoss = Hydra.ACDID_INVALID;

-- From POWER_ERUPTION
S_POWER_ERRUPTION_BUFF_WORLDBOSS_ACTIVE = "WORLDBOSS_ACTIVE";
S_AG_POWER_ERRUPTION_MONSTERS = "UberSubzone_Harbinger";

-- Despawning Monsters Power
local POWER_DESPAWN_MONSTER = Hydra.ReferencePower("Generic_Blood_Despawn");
S_POWER_DESPAWN_START_VAR_DELAY = "DelayDespawn";

function DespawnPowerEruptionMonstersFromWorldBoss()
	if Hydra.ActorIsAlive(idWorldBoss) then
		local tPowerErruptionMonsters = Hydra.ActorsInGroup(S_AG_POWER_ERRUPTION_MONSTERS);
		if #tPowerErruptionMonsters > 0 then
			for i, idMonster in ipairs(tPowerErruptionMonsters) do

				-- Despawn when Monster is alive, share same world with World Boss, share same level area with World Boss
				if Hydra.ActorIsAlive(idMonster) and Hydra.ActorsInSameWorld(idMonster, idWorldBoss) and Hydra.ActorsInSameLevelArea(idMonster, idWorldBoss) then
					Hydra.StartPowerVariant(idMonster, POWER_DESPAWN_MONSTER, S_POWER_DESPAWN_START_VAR_DELAY, 0.0, 1.0);
				end

			end
		end
	end
end
